mapper.select:
select a.anum, a.name, r.price, a.image, re.sido || ' ' || re.sigungu || ' ' || re.bname || ' ' || re.bname2 as region from( select a.anum, min(r.price) as price from accom a inner join room r on a.anum = r.anum inner join region re on a.rcode = re.rcode where 1 = 1
and a.anum in ( with a as ( select anum, regexp_substr(fac1, '[^,]+', 1, level) as fac from accom a connect by level <= regexp_count(fac1, '[^,]+') and prior a.cnum = a.cnum and prior dbms_random.value is not null union all select anum, regexp_substr(fac2, '[^,]+', 1, level) from accom a connect by level <= regexp_count(fac2, '[^,]+') and prior a.cnum = a.cnum and prior dbms_random.value is not null ) select anum from a where fac in ( select regexp_substr(#{fac}, '[^,]+', 1, level) from dual connect by level <= (select regexp_count(#{fac}, '[^,]+') from dual) ) group by anum having count(fac) = (select regexp_count(#{fac}, '[^,]+') from dual) )
and r.rnum not in ( select rnum from orders where to_date(startDate, 'yyyyMMdd') <= to_date(#{end}, 'yyyyMMdd') and to_date(endDate, 'yyyyMMdd') >= to_date(#{start}, 'yyyyMMdd') )
and r.max >= #{count}
and r.price <= #{price}
and a.canum = #{canum}
and substr(re.rcode, 0, 5) = #{rcode}
and substr(re.rcode, 1, 2) = substr(#{rcode}, 1, 2)
group by a.anum ) z inner join room r on z.price = r.price and z.anum = r.anum inner join accom a on z.anum = a.anum inner join region re on a.rcode = re.rcode
order by price desc
order by price
