mapper.select:
select a.anum, a.name, a.addr, r.price, a.image from( select r.rnum, min(r.price) from accom a inner join room r on a.anum = r.anum where 1 = 1
and a.anum in( select anum from( select anum, cnum, regexp_substr(fac2, '[^,]+', 1, level) fac2 from accom a connect by level <= regexp_count(fac2, '[^,]+') and prior a.cnum = a.cnum and prior dbms_random.value is not null ) where fac2 in ( select regexp_substr(#{fac2}, '[^,]+', 1, level) from dual connect by level <= (select regexp_count(#{fac2}, '[^,]+') from dual) ) group by anum having count(anum) = (select regexp_count(#{fac2}, '[^,]+') from dual) )
and a.anum in( select anum from( select anum, cnum, regexp_substr(fac1, '[^,]+', 1, level) fac1 from accom a connect by level <= regexp_count(fac1, '[^,]+') and prior a.cnum = a.cnum and prior dbms_random.value is not null ) where fac1 in ( select regexp_substr(#{fac1}, '[^,]+', 1, level) from dual connect by level <= (select regexp_count(#{fac1}, '[^,]+') from dual) ) group by anum having count(anum) = (select regexp_count(#{fac1}, '[^,]+') from dual) )
and r.rnum not in ( select rnum from orders where to_date(startDate, 'yyyyMMdd') <= to_date(#{end}, 'yyyyMMdd') and to_date(endDate, 'yyyyMMdd') >= to_date(#{start}, 'yyyyMMdd') )
and r.max >= #{count}
and r.price <= #{price}
and a.rnum = #{resion}
group by r.rnum ) z inner join room r on z.rnum = r.rnum inner join accom a on r.anum = a.anum
